    const Application=()=>{
        const [counter, setCounter] =useState(0)
        const startCounter = ()=>{
            setInterval(()=>{
                setCounter(prevNum=>prevNum+1);
            },1000);
        }
    
  return (
  <div>
      <h1> for {counter}</h1>
      <button onClick={startCounter}>Start</button>
  </div>
  )
  }
  
